import React, { useState, useMemo } from 'react';
import { 
  ShoppingBasket, PlusCircle, History, Tag, Ship, 
  ChevronRight, Handshake, Users, Trash2, Fish, 
  Scale, Clock, CheckCircle2, AlertCircle, ArrowLeft, Info,
  Search, Phone, Filter, TrendingUp, Sparkles, X, Camera,
  MapPin, Award, ShoppingCart
} from 'lucide-react';

/**
 * KADALSATHI MARKETPLACE - HIGH FIDELITY VERSION
 * Features: Visual Catalogs, Dynamic Pricing, Urgent Orders, and Localized Trade.
 */

const App = () => {
  // --- STATE ---
  const [isMalayalam, setIsMalayalam] = useState(false);
  const [marketTab, setMarketTab] = useState('browse'); // Default to browse for visual impact
  const [searchQuery, setSearchQuery] = useState("");
  
  // Enhanced Simulation Data with Images
  const [marketListings, setMarketListings] = useState([
    { 
      id: 1, 
      fish: "King Fish (Neymeen)", 
      weight: "15", 
      time: new Date(Date.now() - 3600000), 
      seller: "Sagar Boat", 
      price: "480", 
      unit: "kg",
      img: "data:image/jpeg;base64,",
      isVerified: true
    },
    { 
      id: 2, 
      fish: "Sardines (Mathi)", 
      weight: "120", 
      time: new Date(Date.now() - 7200000), 
      seller: "Minu Marina", 
      price: "140", 
      unit: "kg",
      img: "data:image/jpeg;base64,",
      isVerified: false
    },
    { 
      id: 3, 
      fish: "Prawns (Chemmeen)", 
      weight: "12", 
      time: new Date(), 
      seller: "BlueWave Express", 
      price: "650", 
      unit: "kg",
      img: "data:image/jpeg;base64,",
      isVerified: true
    }
  ]);

  const [publicOrders, setPublicOrders] = useState([
    { id: 101, client: "Beach Resort Calicut", item: "Tuna (Choora)", qty: "25kg", status: "Open", urgent: true },
    { id: 102, client: "Kozhikode Central Kitchen", item: "Mackerel (Ayala)", qty: "50kg", status: "Open", urgent: false }
  ]);

  // Form State
  const [fishName, setFishName] = useState("");
  const [weight, setWeight] = useState("");
  const [price, setPrice] = useState("");

  const handlePostCatch = (e) => {
    e.preventDefault();
    if (!fishName || !weight || !price) return;

    const newListing = {
      id: Date.now(),
      fish: fishName,
      weight: weight,
      time: new Date(),
      seller: "My Boat (KDL-88)",
      price: price,
      unit: "kg",
      img: "data:image/jpeg;base64,", // Generic placeholder for new post
      isVerified: false
    };

    setMarketListings([newListing, ...marketListings]);
    setFishName(""); setWeight(""); setPrice("");
    setMarketTab('browse'); 
  };

  const claimOrder = (id) => {
    setPublicOrders(publicOrders.map(order => 
      order.id === id ? { ...order, status: "Claimed" } : order
    ));
  };

  const filteredListings = useMemo(() => {
    return marketListings.filter(item => 
      item.fish.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [marketListings, searchQuery]);

  const getFreshness = (time) => {
    const mins = Math.floor((new Date() - new Date(time)) / 60000);
    if (mins < 30) return { label: "Just Landed", color: "bg-emerald-500", icon: <Sparkles size={10} /> };
    if (mins < 120) return { label: "Freshly Caught", color: "bg-blue-500", icon: <Clock size={10} /> };
    return { label: "Logged Today", color: "bg-slate-400", icon: <History size={10} /> };
  };

  const t = {
    title: isMalayalam ? " " : "KadalSathi Market",
    sell: isMalayalam ? " " : "Post Your Catch",
    browse: isMalayalam ? " " : "Live Market",
    search: isMalayalam ? " ..." : "Search fresh catch...",
    orders: isMalayalam ? " " : "Buyer Requests",
    post: isMalayalam ? " " : "Upload to Market",
    contact: isMalayalam ? "" : "Contact Seller",
    claim: isMalayalam ? "" : "Claim Order",
    claimed: isMalayalam ? "" : "Claimed"
  };

  return (
    <div className="max-w-md mx-auto min-h-screen bg-slate-50 flex flex-col font-sans antialiased pb-10 border-x border-slate-200 shadow-2xl relative overflow-x-hidden">
      
      {/* 1. PREMIUM HEADER */}
      <header className="bg-white/80 backdrop-blur-md p-5 flex justify-between items-center border-b sticky top-0 z-50">
        <div className="flex items-center gap-4">
          <div className="bg-gradient-to-br from-blue-600 to-blue-800 p-2.5 rounded-2xl text-white shadow-xl shadow-blue-200">
            <ShoppingBasket size={24} />
          </div>
          <div>
            <h1 className="text-xl font-black text-slate-900 leading-none tracking-tight">{t.title}</h1>
            <div className="flex items-center gap-1.5 mt-1">
              <div className="w-2 h-2 rounded-full bg-emerald-500 animate-pulse"></div>
              <p className="text-[10px] font-black text-slate-500 uppercase tracking-widest">Calicut Harbour  Live</p>
            </div>
          </div>
        </div>
        <button 
          onClick={() => setIsMalayalam(!isMalayalam)}
          className="bg-slate-900 text-white px-3 py-1.5 rounded-xl font-black text-[10px] uppercase shadow-lg active:scale-95 transition-all"
        >
          {isMalayalam ? "EN" : ""}
        </button>
      </header>

      {/* 2. MARKET TABS */}
      <div className="flex px-4 pt-4 gap-3 bg-white pb-4 border-b">
        <button 
          onClick={() => setMarketTab('browse')}
          className={`flex-1 flex items-center justify-center gap-2 py-4 rounded-2xl font-black text-xs uppercase tracking-wider transition-all ${marketTab === 'browse' ? 'bg-blue-600 text-white shadow-xl shadow-blue-100 scale-105' : 'bg-slate-50 text-slate-400'}`}
        >
          <Ship size={16} /> {t.browse}
        </button>
        <button 
          onClick={() => setMarketTab('sell')}
          className={`flex-1 flex items-center justify-center gap-2 py-4 rounded-2xl font-black text-xs uppercase tracking-wider transition-all ${marketTab === 'sell' ? 'bg-blue-600 text-white shadow-xl shadow-blue-100 scale-105' : 'bg-slate-50 text-slate-400'}`}
        >
          <PlusCircle size={16} /> {t.sell}
        </button>
      </div>

      <main className="p-4 flex-grow space-y-6">
        
        {marketTab === 'browse' ? (
          <div className="space-y-6 animate-in fade-in slide-in-from-right duration-500">
            
            {/* SEARCH & FILTERS */}
            <div className="flex gap-2">
              <div className="relative flex-grow">
                <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                <input 
                  type="text"
                  placeholder={t.search}
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full bg-white border-2 border-slate-100 rounded-2xl py-4 pl-12 pr-4 font-bold shadow-sm focus:border-blue-500 outline-none transition-all"
                />
              </div>
              <button className="bg-white border-2 border-slate-100 p-4 rounded-2xl text-slate-600 shadow-sm active:bg-slate-50">
                <Filter size={20} />
              </button>
            </div>

            {/* LIVE LISTINGS GRID */}
            <div className="space-y-4">
              <div className="flex justify-between items-center px-1">
                <h3 className="text-[11px] font-black text-slate-400 uppercase tracking-widest flex items-center gap-2">
                  Featured Fresh Arrivals
                </h3>
                <TrendingUp size={14} className="text-blue-500" />
              </div>

              {filteredListings.map(item => {
                const fresh = getFreshness(item.time);
                return (
                  <div key={item.id} className="bg-white rounded-[2rem] border border-slate-100 shadow-sm overflow-hidden group active:scale-[0.98] transition-all">
                    <div className="relative h-48 overflow-hidden">
                      <img 
                        src={item.img} 
                        alt={item.fish}
                        className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-700"
                        onError={(e) => { e.target.src = 'https://source.unsplash.com/800x600/?fishing,harbour,fish'; }}
                      />
                      <div className="absolute top-4 left-4 flex gap-2">
                        <span className={`${fresh.color} text-white text-[9px] font-black px-3 py-1 rounded-full uppercase flex items-center gap-1 shadow-lg`}>
                          {fresh.icon} {fresh.label}
                        </span>
                        {item.isVerified && (
                          <span className="bg-blue-600 text-white text-[9px] font-black px-3 py-1 rounded-full uppercase flex items-center gap-1 shadow-lg">
                            <Award size={10} /> Verified
                          </span>
                        )}
                      </div>
                      <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-4">
                        <p className="text-white font-black text-xl leading-tight">{item.fish}</p>
                        <div className="flex items-center gap-2 text-white/70 text-[10px] font-bold uppercase mt-1">
                          <MapPin size={10} /> {item.seller}
                        </div>
                      </div>
                    </div>
                    
                    <div className="p-5 flex items-center justify-between">
                      <div className="space-y-1">
                         <div className="flex items-center gap-2 text-slate-400 font-bold text-xs">
                            <Scale size={14} /> {item.weight} {item.unit} available
                         </div>
                         <p className="text-2xl font-black text-blue-600 tracking-tighter">
                            {item.price}<span className="text-xs text-slate-400 font-bold ml-1">/kg</span>
                         </p>
                      </div>
                      <button className="bg-slate-900 text-white h-12 px-6 rounded-2xl font-black text-xs uppercase flex items-center gap-2 shadow-xl active:bg-blue-600 transition-colors">
                        <Phone size={14} fill="white" /> {t.contact}
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>

            {/* THE "SPECIAL SPACE": PUBLIC ORDERS */}
            <div className="bg-slate-900 rounded-[2.5rem] p-7 shadow-2xl text-white relative overflow-hidden">
              <div className="absolute top-0 right-0 w-40 h-40 bg-blue-600/10 rounded-full -mr-20 -mt-20 blur-3xl"></div>
              <Handshake className="absolute -right-8 -top-8 w-48 h-48 opacity-10 rotate-12" />
              
              <div className="relative z-10 mb-6">
                <div className="flex items-center gap-3 mb-1">
                  <Users size={20} className="text-blue-400" />
                  <h3 className="text-2xl font-black uppercase tracking-tight italic">{t.orders}</h3>
                </div>
                <p className="text-[10px] font-black text-slate-500 uppercase tracking-[0.2em]">Verified B2B Requirements</p>
              </div>

              <div className="space-y-4 relative z-10">
                {publicOrders.map(order => (
                  <div key={order.id} className="bg-white/5 backdrop-blur-xl rounded-[1.5rem] p-5 border border-white/10 flex items-center justify-between hover:bg-white/10 transition-all">
                    <div>
                      <p className="font-black text-base flex items-center gap-2">
                        {order.item}
                        {order.urgent && <span className="bg-red-500 text-[8px] px-1.5 py-0.5 rounded animate-pulse">URGENT</span>}
                      </p>
                      <div className="flex items-center gap-2 mt-1 opacity-60">
                        <ShoppingCart size={12} className="text-blue-400" />
                        <p className="text-[10px] font-bold uppercase tracking-wider">{order.qty}  {order.client}</p>
                      </div>
                    </div>
                    <button 
                      onClick={() => claimOrder(order.id)}
                      disabled={order.status === 'Claimed'}
                      className={`px-6 py-3 rounded-2xl text-[10px] font-black uppercase transition-all shadow-xl ${order.status === 'Claimed' ? 'bg-emerald-500 text-white cursor-default' : 'bg-white text-slate-900 active:scale-95'}`}
                    >
                      {order.status === 'Claimed' ? (
                        <span className="flex items-center gap-1"><CheckCircle2 size={12}/> {t.claimed}</span>
                      ) : t.claim}
                    </button>
                  </div>
                ))}
              </div>
            </div>

          </div>
        ) : (
          /* SELL CATCH VIEW */
          <div className="animate-in fade-in slide-in-from-left duration-500">
            <div className="bg-white rounded-[2.5rem] p-8 shadow-sm border border-slate-100">
              <div className="flex items-center gap-4 mb-8">
                <div className="p-3 bg-blue-50 text-blue-600 rounded-2xl shadow-inner"><Camera size={28}/></div>
                <div>
                  <h2 className="text-2xl font-black text-slate-900 tracking-tight">{t.sell}</h2>
                  <p className="text-xs font-bold text-slate-400 uppercase">List your catch live from sea</p>
                </div>
              </div>

              <form onSubmit={handlePostCatch} className="space-y-6">
                <div className="space-y-2">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] block ml-1">Fish Details</label>
                  <input 
                    type="text"
                    value={fishName}
                    onChange={(e) => setFishName(e.target.value)}
                    placeholder="e.g. Tuna (Choora)"
                    className="w-full bg-slate-50 border-2 border-slate-100 rounded-[1.5rem] p-5 font-bold text-lg focus:border-blue-500 outline-none transition-all placeholder:text-slate-300"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] block ml-1">Weight (kg)</label>
                    <div className="relative">
                      <input 
                        type="number"
                        value={weight}
                        onChange={(e) => setWeight(e.target.value)}
                        placeholder="00"
                        className="w-full bg-slate-50 border-2 border-slate-100 rounded-[1.5rem] p-5 font-black text-xl focus:border-blue-500 outline-none transition-all"
                      />
                      <Scale className="absolute right-5 top-1/2 -translate-y-1/2 text-slate-300" size={20} />
                    </div>
                  </div>
                  <div className="space-y-2">
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] block ml-1">Price (/kg)</label>
                    <div className="relative">
                      <input 
                        type="number"
                        value={price}
                        onChange={(e) => setPrice(e.target.value)}
                        placeholder="0"
                        className="w-full bg-slate-50 border-2 border-slate-100 rounded-[1.5rem] p-5 font-black text-xl focus:border-blue-500 outline-none transition-all text-emerald-600"
                      />
                      <Tag className="absolute right-5 top-1/2 -translate-y-1/2 text-slate-300" size={20} />
                    </div>
                  </div>
                </div>

                <div className="bg-blue-50/50 p-5 rounded-[1.5rem] border border-blue-100/50 flex items-start gap-4">
                   <Info size={18} className="text-blue-600 shrink-0 mt-0.5" />
                   <p className="text-[10px] font-bold text-blue-800/70 leading-relaxed uppercase tracking-wide">
                     Your location and time will be automatically tagged to verify freshness for buyers.
                   </p>
                </div>

                <button 
                  type="submit"
                  className="w-full py-6 bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-[2rem] font-black uppercase text-base shadow-2xl shadow-blue-200 active:scale-[0.97] active:shadow-inner transition-all flex items-center justify-center gap-3"
                >
                  <Sparkles size={24} />
                  {t.post}
                </button>
              </form>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default App;